package com.shape.home.menu;import android.content.Context;import android.graphics.LinearGradient;import android.graphics.Path;import vimalcvs.api.ShapeView;import vimalcvs.api.shapeImg;import android.graphics.Paint;import android.graphics.Canvas;import com.vimal.player.Ui;public class playBtn extends shapeImg{ class Path0 extends Path{  	public Path0() {			moveTo(50f, 13f);			lineTo(50f, 37f);			quadTo(50f, 50f, 37f, 50f);			lineTo(13f, 50f);			quadTo(0f, 50f, 0f, 37f);			lineTo(0f, 13f);			quadTo(0f, 0f, 13f, 0f);			lineTo(37f, 0f);			quadTo(50f, 0f, 50f, 13f);	}}class Path1 extends Path{  	public Path1() {			moveTo(48f, 37f);			quadTo(48f, 48f, 37f, 48f);			lineTo(13f, 48f);			quadTo(2f, 48f, 2f, 37f);			lineTo(2f, 13f);			quadTo(2f, 2f, 13f, 2f);			lineTo(37f, 2f);			quadTo(48f, 2f, 48f, 13f);			lineTo(48f, 37f);	}}class Path2 extends Path{  	public Path2() {			moveTo(30.25f, 22.85f);			quadTo(32.6f, 24.85f, 30.25f, 26.8f);			lineTo(23.3f, 33.8f);			quadTo(22.8f, 34.35f, 22.05f, 34.35f);			quadTo(21.3f, 34.35f, 20.7f, 33.8f);			quadTo(20.25f, 33.3f, 20.2f, 32.7f);			lineTo(20.2f, 17.2f);			lineTo(20.25f, 17.2f);			quadTo(20.35f, 16.65f, 20.7f, 16.2f);			quadTo(21.3f, 15.65f, 22.05f, 15.65f);			quadTo(22.8f, 15.65f, 23.3f, 16.2f);			lineTo(30.25f, 22.85f);	}}		public Paint P0 = new Paint(); 		public static int Color0 = (0xFFd35d69); 		public Path0 S0 = new Path0();		public Paint P1 = new Paint(); 		public static int Color1 = (0xFF524776); 		public Path1 S1 = new Path1();		public Paint P2 = new Paint(); 		public static int Color2 = (0xFFffffff); 		public Path2 S2 = new Path2();		public static float Ht = 50F; 		public static float Wh = 50F;  		public playBtn(int width,int height,int x,int y) {			this.width = width;			this.height = height;			init((float)width/(float)Wh, (float)height/(float)Ht, x, y); 			LinearGradient Lg  = null;			S0.transform(matrix);			P0.setColor(0xFFd35d69);			P0.setAntiAlias(true);			S1.transform(matrix);			P1.setColor(0xFF524776);			P1.setAntiAlias(true);			S2.transform(matrix);			P2.setColor(0xFFffffff);			P2.setAntiAlias(true);			mask = S0;			maskPaint = P0;		} 		public void draw(Canvas canvas) { 			if(drawing){				canvas.drawPath(S0, P0);				canvas.drawPath(S1, P1);				canvas.drawPath(S2, P2);			}		} 		@Override 		public void setX(int x) {			super.setX(x);			 S0.transform(matrix);			 S1.transform(matrix);			 S2.transform(matrix);		} 		@Override 		public void setY(int x) {			super.setY(x);			 S0.transform(matrix);			 S1.transform(matrix);			 S2.transform(matrix);		} 		@Override 		public void setSize(int wh, int ht) {			super.setSize(wh, ht);			 S0.transform(matrix);			 S1.transform(matrix);			 S2.transform(matrix);		} 		 public static ShapeView getFMview(Context context,boolean mask){		   ShapeView view = new ShapeView(context, Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht));		   final playBtn viewImg = new playBtn(Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht),0,0);		   view.mask = mask;		   view.img = viewImg;		   viewImg.mask = viewImg.S0;		   viewImg.maskPaint = viewImg.P0; 		   return view;		 }      		 public static playBtn getShape(){		   final playBtn viewImg = new playBtn(Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht),0,0);		   viewImg.mask = viewImg.S0;		   viewImg.maskPaint = viewImg.P0; 		   return viewImg;		 }     }//Finished...! 