package com.shape.home.playlist.popup;import android.content.Context;import android.graphics.LinearGradient;import android.graphics.Path;import vimalcvs.api.ShapeView;import vimalcvs.api.shapeImg;import android.graphics.Paint;import android.graphics.Canvas;import com.vimal.player.Ui;public class selectedBtn extends shapeImg{ class Path0 extends Path{  	public Path0() {			moveTo(21.05f, 21.05f);			quadTo(17.45f, 24.7f, 12.35f, 24.7f);			quadTo(7.25f, 24.7f, 3.6f, 21.05f);			quadTo(0f, 17.45f, 0f, 12.35f);			quadTo(0f, 7.25f, 3.6f, 3.6f);			quadTo(7.25f, 0f, 12.35f, 0f);			quadTo(17.45f, 0f, 21.05f, 3.6f);			quadTo(24.7f, 7.25f, 24.7f, 12.35f);			quadTo(24.7f, 17.45f, 21.05f, 21.05f);	}}class Path1 extends Path{  	public Path1() {			moveTo(8.7f, 16.55f);			lineTo(8.75f, 16.55f);			lineTo(4.65f, 12.45f);			lineTo(6.1f, 11f);			lineTo(10.2f, 15.1f);			lineTo(18.6f, 6.7f);			lineTo(20.05f, 8.15f);			lineTo(10.15f, 18f);			lineTo(8.7f, 16.55f);	}}		public Paint P0 = new Paint(); 		public static int Color0 = (0x33ffffff); 		public Path0 S0 = new Path0();		public Paint P1 = new Paint(); 		public static int Color1 = (0xFFffffff); 		public Path1 S1 = new Path1();		public static float Ht = 24.7F; 		public static float Wh = 24.7F;  		public selectedBtn(int width,int height,int x,int y) {			this.width = width;			this.height = height;			init((float)width/(float)Wh, (float)height/(float)Ht, x, y); 			LinearGradient Lg  = null;			S0.transform(matrix);			P0.setColor(0x33ffffff);			P0.setAntiAlias(true);			S1.transform(matrix);			P1.setColor(0xFFffffff);			P1.setAntiAlias(true);			mask = S0;			maskPaint = P0;		} 		public void draw(Canvas canvas) { 			if(drawing){				canvas.drawPath(S0, P0);				canvas.drawPath(S1, P1);			}		} 		@Override 		public void setX(int x) {			super.setX(x);			 S0.transform(matrix);			 S1.transform(matrix);		} 		@Override 		public void setY(int x) {			super.setY(x);			 S0.transform(matrix);			 S1.transform(matrix);		} 		@Override 		public void setSize(int wh, int ht) {			super.setSize(wh, ht);			 S0.transform(matrix);			 S1.transform(matrix);		} 		 public static ShapeView getFMview(Context context,boolean mask){		   ShapeView view = new ShapeView(context, Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht));		   final selectedBtn viewImg = new selectedBtn(Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht),0,0);		   view.mask = mask;		   view.img = viewImg;		   viewImg.mask = viewImg.S0;		   viewImg.maskPaint = viewImg.P0; 		   return view;		 }      		 public static selectedBtn getShape(){		   final selectedBtn viewImg = new selectedBtn(Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht),0,0);		   viewImg.mask = viewImg.S0;		   viewImg.maskPaint = viewImg.P0; 		   return viewImg;		 }     }//Finished...! 