package com.shape.home.songslider;import android.content.Context;import android.graphics.LinearGradient;import android.graphics.Path;import vimalcvs.api.ShapeView;import vimalcvs.api.shapeImg;import android.graphics.Paint;import android.graphics.Canvas;import com.vimal.player.Ui;public class playImg extends shapeImg{ class Path0 extends Path{  	public Path0() {			moveTo(40f, 40f);			lineTo(0f, 40f);			lineTo(0f, 0f);			lineTo(40f, 0f);			lineTo(40f, 40f);	}}class Path1 extends Path{  	public Path1() {			moveTo(27.5f, 22.3f);			lineTo(16.95f, 30.95f);			quadTo(16.25f, 31.65f, 15.1f, 31.65f);			quadTo(13.9f, 31.65f, 13.05f, 30.95f);			quadTo(12.3f, 30.3f, 12.25f, 29.5f);			lineTo(12.25f, 10.3f);			lineTo(12.3f, 10.3f);			quadTo(12.35f, 9.6f, 13.05f, 9f);			quadTo(13.9f, 8.35f, 15.1f, 8.35f);			quadTo(16.25f, 8.35f, 16.95f, 9f);			lineTo(27.5f, 17.4f);			quadTo(31.05f, 19.9f, 27.5f, 22.3f);	}}		public Paint P0 = new Paint(); 		public static int Color0 = (0x01000000); 		public Path0 S0 = new Path0();		public Paint P1 = new Paint(); 		public static int Color1 = (0xFFffffff); 		public Path1 S1 = new Path1();		public static float Ht = 40F; 		public static float Wh = 40F;  		public playImg(int width,int height,int x,int y) {			this.width = width;			this.height = height;			init((float)width/(float)Wh, (float)height/(float)Ht, x, y); 			LinearGradient Lg  = null;			S0.transform(matrix);			P0.setColor(0x01000000);			P0.setAntiAlias(true);			S1.transform(matrix);			P1.setColor(0xFFffffff);			P1.setAntiAlias(true);			mask = S0;			maskPaint = P0;		} 		public void draw(Canvas canvas) { 			if(drawing){				canvas.drawPath(S0, P0);				canvas.drawPath(S1, P1);			}		} 		@Override 		public void setX(int x) {			super.setX(x);			 S0.transform(matrix);			 S1.transform(matrix);		} 		@Override 		public void setY(int x) {			super.setY(x);			 S0.transform(matrix);			 S1.transform(matrix);		} 		@Override 		public void setSize(int wh, int ht) {			super.setSize(wh, ht);			 S0.transform(matrix);			 S1.transform(matrix);		} 		 public static ShapeView getFMview(Context context,boolean mask){		   ShapeView view = new ShapeView(context, Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht));		   final playImg viewImg = new playImg(Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht),0,0);		   view.mask = mask;		   view.img = viewImg;		   viewImg.mask = viewImg.S0;		   viewImg.maskPaint = viewImg.P0; 		   return view;		 }      		 public static playImg getShape(){		   final playImg viewImg = new playImg(Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht),0,0);		   viewImg.mask = viewImg.S0;		   viewImg.maskPaint = viewImg.P0; 		   return viewImg;		 }     }//Finished...! 