package com.shape.library.btm;import android.content.Context;import android.graphics.LinearGradient;import android.graphics.Path;import vimalcvs.api.ShapeView;import vimalcvs.api.shapeImg;import android.graphics.Paint;import android.graphics.Canvas;import com.vimal.player.Ui;public class prevBtn extends shapeImg{ class Path0 extends Path{  	public Path0() {			moveTo(13.65f, 1.65f);			lineTo(13.65f, 17.1f);			lineTo(13.6f, 17.1f);			lineTo(13.15f, 18.1f);			quadTo(12.55f, 18.65f, 11.8f, 18.65f);			quadTo(11.05f, 18.65f, 10.55f, 18.1f);			lineTo(3.65f, 11.45f);			quadTo(1.3f, 9.5f, 3.65f, 7.55f);			lineTo(10.55f, 0.55f);			quadTo(11.05f, 0f, 11.8f, 0f);			quadTo(12.55f, 0f, 13.15f, 0.55f);			quadTo(13.6f, 1.05f, 13.65f, 1.65f);	}}class Path1 extends Path{  	public Path1() {			moveTo(2f, 17.55f);			lineTo(1.7f, 18.25f);			lineTo(1f, 18.55f);			quadTo(0.6f, 18.55f, 0.3f, 18.25f);			quadTo(0f, 17.95f, 0f, 17.55f);			lineTo(0f, 1.15f);			quadTo(0f, 0.75f, 0.3f, 0.45f);			quadTo(0.6f, 0.15f, 1f, 0.15f);			quadTo(1.4f, 0.15f, 1.7f, 0.45f);			lineTo(2f, 1.15f);			lineTo(2f, 17.55f);	}}		public Paint P0 = new Paint(); 		public static int Color0 = (0xFFffffff); 		public Path0 S0 = new Path0();		public Paint P1 = new Paint(); 		public static int Color1 = (0xFFffffff); 		public Path1 S1 = new Path1();		public static float Ht = 18.65F; 		public static float Wh = 13.65F;  		public prevBtn(int width,int height,int x,int y) {			this.width = width;			this.height = height;			init((float)width/(float)Wh, (float)height/(float)Ht, x, y); 			LinearGradient Lg  = null;			S0.transform(matrix);			P0.setColor(0xFFffffff);			P0.setAntiAlias(true);			S1.transform(matrix);			P1.setColor(0xFFffffff);			P1.setAntiAlias(true);			mask = S0;			maskPaint = P0;		} 		public void draw(Canvas canvas) { 			if(drawing){				canvas.drawPath(S0, P0);				canvas.drawPath(S1, P1);			}		} 		@Override 		public void setX(int x) {			super.setX(x);			 S0.transform(matrix);			 S1.transform(matrix);		} 		@Override 		public void setY(int x) {			super.setY(x);			 S0.transform(matrix);			 S1.transform(matrix);		} 		@Override 		public void setSize(int wh, int ht) {			super.setSize(wh, ht);			 S0.transform(matrix);			 S1.transform(matrix);		} 		 public static ShapeView getFMview(Context context,boolean mask){		   ShapeView view = new ShapeView(context, Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht));		   final prevBtn viewImg = new prevBtn(Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht),0,0);		   view.mask = mask;		   view.img = viewImg;		   viewImg.mask = viewImg.S0;		   viewImg.maskPaint = viewImg.P0; 		   return view;		 }      		 public static prevBtn getShape(){		   final prevBtn viewImg = new prevBtn(Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht),0,0);		   viewImg.mask = viewImg.S0;		   viewImg.maskPaint = viewImg.P0; 		   return viewImg;		 }     }//Finished...! 