package com.shape.library.popup;import android.content.Context;import android.graphics.LinearGradient;import android.graphics.Path;import vimalcvs.api.ShapeView;import vimalcvs.api.shapeImg;import android.graphics.Paint;import android.graphics.Canvas;import com.vimal.player.Ui;public class mainBackground extends shapeImg{ class Path0 extends Path{  	public Path0() {			moveTo(237f, 0f);			quadTo(250f, 0f, 250f, 13f);			lineTo(250f, 317f);			quadTo(250f, 330f, 237f, 330f);			lineTo(13f, 330f);			quadTo(0f, 330f, 0f, 317f);			lineTo(0f, 13f);			quadTo(0f, 0f, 13f, 0f);			lineTo(237f, 0f);	}}class Path1 extends Path{  	public Path1() {			moveTo(236.5f, 2f);			quadTo(248f, 2f, 248f, 13.5f);			lineTo(248f, 316.5f);			quadTo(248f, 328f, 236.5f, 328f);			lineTo(13.5f, 328f);			quadTo(2f, 328f, 2f, 316.5f);			lineTo(2f, 13.5f);			quadTo(2f, 2f, 13.5f, 2f);			lineTo(236.5f, 2f);	}}		public Paint P0 = new Paint(); 		public static int Color0 = (0xFF51487d); 		public Path0 S0 = new Path0();		public Paint P1 = new Paint(); 		public static int Color1 = (0xFF352f51); 		public Path1 S1 = new Path1();		public static float Ht = 330F; 		public static float Wh = 250F;  		public mainBackground(int width,int height,int x,int y) {			this.width = width;			this.height = height;			init((float)width/(float)Wh, (float)height/(float)Ht, x, y); 			LinearGradient Lg  = null;			S0.transform(matrix);			P0.setColor(0xFF51487d);			P0.setAntiAlias(true);			S1.transform(matrix);			P1.setColor(0xFF352f51);			P1.setAntiAlias(true);			mask = S0;			maskPaint = P0;		} 		public void draw(Canvas canvas) { 			if(drawing){				canvas.drawPath(S0, P0);				canvas.drawPath(S1, P1);			}		} 		@Override 		public void setX(int x) {			super.setX(x);			 S0.transform(matrix);			 S1.transform(matrix);		} 		@Override 		public void setY(int x) {			super.setY(x);			 S0.transform(matrix);			 S1.transform(matrix);		} 		@Override 		public void setSize(int wh, int ht) {			super.setSize(wh, ht);			 S0.transform(matrix);			 S1.transform(matrix);		} 		 public static ShapeView getFMview(Context context,boolean mask){		   ShapeView view = new ShapeView(context, Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht));		   final mainBackground viewImg = new mainBackground(Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht),0,0);		   view.mask = mask;		   view.img = viewImg;		   viewImg.mask = viewImg.S0;		   viewImg.maskPaint = viewImg.P0; 		   return view;		 }      		 public static mainBackground getShape(){		   final mainBackground viewImg = new mainBackground(Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht),0,0);		   viewImg.mask = viewImg.S0;		   viewImg.maskPaint = viewImg.P0; 		   return viewImg;		 }     }//Finished...! 