package com.shape.notification;import android.content.Context;import android.graphics.LinearGradient;import android.graphics.Path;import vimalcvs.api.ShapeView;import vimalcvs.api.shapeImg;import android.graphics.Paint;import android.graphics.Canvas;import com.vimal.player.Ui;public class closeBtn extends shapeImg{ class Path0 extends Path{  	public Path0() {			moveTo(40f, 64f);			lineTo(0f, 64f);			lineTo(0f, 0f);			lineTo(40f, 0f);			lineTo(40f, 64f);	}}class Path1 extends Path{  	public Path1() {			moveTo(38f, 13f);			lineTo(38f, 31f);			quadTo(38f, 42f, 27f, 42f);			lineTo(13f, 42f);			quadTo(2f, 42f, 2f, 31f);			lineTo(2f, 13f);			quadTo(2f, 2f, 13f, 2f);			lineTo(27f, 2f);			quadTo(38f, 2f, 38f, 13f);	}}class Path2 extends Path{  	public Path2() {			moveTo(26f, 17.8f);			lineTo(21.65f, 22.1f);			lineTo(25.95f, 26.35f);			lineTo(24.45f, 27.85f);			lineTo(20.15f, 23.6f);			lineTo(15.6f, 28.2f);			lineTo(14.05f, 26.7f);			lineTo(18.65f, 22.1f);			lineTo(14f, 17.45f);			lineTo(15.5f, 15.95f);			lineTo(20.15f, 20.6f);			lineTo(24.5f, 16.3f);			lineTo(26f, 17.8f);	}}		public Paint P0 = new Paint(); 		public static int Color0 = (0x0054497c); 		public Path0 S0 = new Path0();		public Paint P1 = new Paint(); 		public static int Color1 = (0x00d35d69); 		public Path1 S1 = new Path1();		public Paint P2 = new Paint(); 		public static int Color2 = (0xFF000000); 		public Path2 S2 = new Path2();		public static float Ht = 64F; 		public static float Wh = 40F;  		public closeBtn(int width,int height,int x,int y) {			this.width = width;			this.height = height;			init((float)width/(float)Wh, (float)height/(float)Ht, x, y); 			LinearGradient Lg  = null;			S0.transform(matrix);			P0.setColor(0x0054497c);			P0.setAntiAlias(true);			S1.transform(matrix);			P1.setColor(0x00d35d69);			P1.setAntiAlias(true);			S2.transform(matrix);			P2.setColor(0xFF000000);			P2.setAntiAlias(true);			mask = S0;			maskPaint = P0;		} 		public void draw(Canvas canvas) { 			if(drawing){				canvas.drawPath(S0, P0);				canvas.drawPath(S1, P1);				canvas.drawPath(S2, P2);			}		} 		@Override 		public void setX(int x) {			super.setX(x);			 S0.transform(matrix);			 S1.transform(matrix);			 S2.transform(matrix);		} 		@Override 		public void setY(int x) {			super.setY(x);			 S0.transform(matrix);			 S1.transform(matrix);			 S2.transform(matrix);		} 		@Override 		public void setSize(int wh, int ht) {			super.setSize(wh, ht);			 S0.transform(matrix);			 S1.transform(matrix);			 S2.transform(matrix);		} 		 public static ShapeView getFMview(Context context,boolean mask){		   ShapeView view = new ShapeView(context, Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht));		   final closeBtn viewImg = new closeBtn(Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht),0,0);		   view.mask = mask;		   view.img = viewImg;		   viewImg.mask = viewImg.S0;		   viewImg.maskPaint = viewImg.P0; 		   return view;		 }      		 public static closeBtn getShape(){		   final closeBtn viewImg = new closeBtn(Ui.cd.getHt((int) Wh), Ui.cd.getHt((int) Ht),0,0);		   viewImg.mask = viewImg.S0;		   viewImg.maskPaint = viewImg.P0; 		   return viewImg;		 }     }//Finished...! 